CREATE DATABASE anime_planet;

\c anime_planet

DROP TABLE IF EXISTS Animes CASCADE;
CREATE TABLE Animes (
    "title" VARCHAR(255) NOT NULL,
    "year" INT NOT NULL,
    "type" VARCHAR(25),
    "episode_count" INT,
    "studio" VARCHAR(255),
    PRIMARY KEY ("title", "year")

    -- Seharusnya constraint di bawah ini diterapkan pada SQL,
    -- tetapi karena Studios merupakan relasi tambahan yang dikosongkan
    -- maka Constraint di-comment out agar tidak error

    -- CONSTRAINT "fk_animes_studios"
    -- FOREIGN KEY ("studio", "studio_founder")
    -- REFERENCES Studios ("romanized_name", "founder")
    -- ON DELETE CASCADE
    -- ON UPDATE CASCADE
);

DROP TABLE IF EXISTS Users CASCADE;
CREATE TABLE Users (
    "username" VARCHAR(255) NOT NULL,
    "join_date" DATE NOT NULL,
    "age" INT,
    "gender" CHAR(1),
    PRIMARY KEY ("username"),
    CONSTRAINT "gender_check"
        CHECK("gender" IN ('f', 'm'))
);

DROP TABLE IF EXISTS Characters CASCADE;
CREATE TABLE Characters (
    "name" VARCHAR(255) NOT NULL,
    "char_url" VARCHAR(255),
    "rank" VARCHAR(3),
    "avatar_url" VARCHAR(255),
    "char_hates" INT,
    "char_comments" INT,
    PRIMARY KEY ("name")
);

DROP TABLE IF EXISTS Studios CASCADE;
CREATE TABLE Studios (
    "romanized_name" VARCHAR(255) NOT NULL,
    "founder" VARCHAR(255) NOT NULL,
    "founding_date" DATE,
    "company_type" VARCHAR(255),
    "headquarters" VARCHAR(255),
    PRIMARY KEY ("romanized_name", "founder")
);

DROP TABLE IF EXISTS Custom_Lists CASCADE;
CREATE TABLE Custom_Lists (
    "creator" VARCHAR(255) NOT NULL,
    "list_title" VARCHAR(255) NOT NULL,
    "list_comments" INT,
    "list_likes" INT,
    PRIMARY KEY ("creator", "list_title"),
    CONSTRAINT "fk_customlists_users"
   	 FOREIGN KEY ("creator")
   	 REFERENCES Users ("username")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE
);

DROP TABLE IF EXISTS Reviews CASCADE;
CREATE TABLE Reviews (
    "reviewer" VARCHAR(255) NOT NULL,
    "date" DATE NOT NULL,
    "anime_title" VARCHAR(255) NOT NULL,
    "anime_year" INT NOT NULL,
    "score" FLOAT,
    "first_sentence" TEXT,
    PRIMARY KEY ("reviewer", "date", "anime_title", "anime_year"),
    CONSTRAINT "fk_reviews_users"
   	 FOREIGN KEY ("reviewer")
   	 REFERENCES Users ("username")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE,
    CONSTRAINT "fk_reviews_animes"
   	 FOREIGN KEY ("anime_title", "anime_year")
   	 REFERENCES Animes ("title", "year")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE
);

DROP TABLE IF EXISTS Appears_in CASCADE;
CREATE TABLE Appears_in (
    "anime_title" VARCHAR(255) NOT NULL,
    "anime_year" INT NOT NULL,
    "char_name" VARCHAR(255) NOT NULL,
    PRIMARY KEY ("anime_title", "anime_year", "char_name"),
    CONSTRAINT "fk_appearsin_animes"
   	 FOREIGN KEY ("anime_title", "anime_year")
   	 REFERENCES Animes ("title", "year")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE,
    CONSTRAINT "fk_appearsin_characters"
   	 FOREIGN KEY ("char_name")
   	 REFERENCES Characters ("name")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE
);

DROP TABLE IF EXISTS Animes_in_Lists CASCADE;
CREATE TABLE Animes_in_Lists (
    "creator" VARCHAR(255) NOT NULL,
    "list_title" VARCHAR(255) NOT NULL,
    "anime_title" VARCHAR(255) NOT NULL,
    "anime_year" INT NOT NULL,
    PRIMARY KEY ("anime_title", "anime_year"),
    CONSTRAINT "fk_animesinlist_animes"
   	 FOREIGN KEY ("anime_title", "anime_year")
   	 REFERENCES Animes ("title", "year")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE,
    CONSTRAINT "fk_animesinlist_characters"
   	 FOREIGN KEY ("creator", "list_title")
   	 REFERENCES Custom_Lists ("creator", "list_title")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE
);

DROP TABLE IF EXISTS Characters_Traits CASCADE;
CREATE TABLE Characters_Traits (
    "name" VARCHAR(255) NOT NULL,
    "trait" VARCHAR(255) NOT NULL,
    PRIMARY KEY ("name", "trait"),
    CONSTRAINT "fk_chractertraits_characters"
   	 FOREIGN KEY ("name")
   	 REFERENCES Characters ("name")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE
);

DROP TABLE IF EXISTS Characters_Tags CASCADE;
CREATE TABLE Characters_Tags (
    "name" VARCHAR(255) NOT NULL,
    "tag" VARCHAR(255) NOT NULL,
    PRIMARY KEY ("name", "tag"),
    CONSTRAINT "fk_chracterTags_characters"
   	 FOREIGN KEY ("name")
   	 REFERENCES Characters ("name")
   	 ON DELETE CASCADE
   	 ON UPDATE CASCADE
);

